class Solution:
    def shortestPath(self, grid: List[List[int]], k: int) -> int:
        M, N = len(grid), len(grid[0])
        #if 1 == M and 1 == N:
        #    return 0
        if k >= M + N - 3:
            return M + N - 2
        
        # (i, j, k, step)
        q = deque([(0, 0, k, 0)])
        seen = set()
        dirs = [(-1, 0), (1, 0), (0, -1), (0, 1)]
        while q:
            i, j, r, step = q.popleft()
            if i == M - 1 and j == N - 1:
                return step
            if (i, j, r) not in seen:
                seen.add((i, j, r))
            for di, dj in dirs:
                ni, nj = i + di, j + dj
                if 0 <= ni < M and 0 <= nj < N:
                    if 0 == grid[ni][nj] and (ni, nj, r) not in seen:
                        if ni == M - 1 and nj == N - 1:
                            return step + 1
                        q.append((ni, nj, r, step + 1))
                        seen.add((ni, nj, r))
                    elif 1 == grid[ni][nj] and r > 0 and (ni, nj, r - 1) not in seen:
                        q.append((ni, nj, r - 1, step + 1))
                        seen.add((ni, nj, r - 1))
        return -1

