class Solution:
    def findWords(self, board: List[List[str]], words: List[str]) -> List[str]:
        M, N = len(board), len(board[0])
        ans = set()
        t = Trie()
        for w in words:
            t.insert(w)
        
        def dfs(now, i1, j1, l):
            # len > 10
            if l > 10 or board[i1][j1] not in now.children:
                return 
            
            ch = board[i1][j1]
            now = now.children[ch]
            if now.word != '':
                ans.add(now.word)

            for i2, j2 in [(i1-1, j1), (i1+1, j1), (i1, j1+1), (i1, j1-1)]:
                if 0 <= i2 < M and 0 <= j2 < N:
                    board[i1][j1] = '#'
                    dfs(now, i2, j2, l + 1)
                    board[i1][j1] = ch
        
        for i in range(M):
            for j in range(N):
                dfs(t, i, j, 0)

        return list(ans)


class Trie:
    def __init__(self):
        self.children = defaultdict(Trie)
        self.word = ''
    
    def insert(self, word):
        cur = self
        for ch in word:
            cur = cur.children[ch]
        cur.word = word



'''
Trie+Backtracking, len > 10 + remove visited words and paths in Trie

执行用时：344 ms, 在所有 Python3 提交中击败了69.15% 的用户
内存消耗：15.2 MB, 在所有 Python3 提交中击败了75.21% 的用户
通过测试用例：42 / 42
'''
class Solution:
    def findWords(self, board: List[List[str]], words: List[str]) -> List[str]:
        M, N = len(board), len(board[0])
        ans = []
        t = Trie()
        for w in words:
            t.insert(w)
        
        def dfs(now, i1, j1, l):
            # len > 10
            if l > 10 or board[i1][j1] not in now.children:
                return 
            
            ch = board[i1][j1]
            nxt = now.children[ch]
            if nxt.word != '':
                ans.append(nxt.word)
                nxt.word = ''

            for i2, j2 in [(i1-1, j1), (i1+1, j1), (i1, j1+1), (i1, j1-1)]:
                if 0 <= i2 < M and 0 <= j2 < N:
                    board[i1][j1] = '#'
                    dfs(nxt, i2, j2, l + 1)
                    board[i1][j1] = ch

            if not nxt.children:
                now.children.pop(ch)

        for i in range(M):
            for j in range(N):
                dfs(t, i, j, 0)

        return ans

class Trie:
    def __init__(self):
        self.children = defaultdict(Trie)
        self.word = ''
    
    def insert(self, word):
        cur = self
        for ch in word:
            cur = cur.children[ch]
        cur.word = word
