class Solution(object):
    def uniqueLetterString(self, s):
        """
        :type s: str
        :rtype: int
        """
        def countUniqueChars(part):
            unique_cnt = 0
            count = collections.Counter(part)
            for char, cnt in count.iteritems():
                if cnt == 1:
                    unique_cnt += 1
            return unique_cnt
        
        all_unique = 0
        size = len(s)
        for i in range(size):
            for j in range(i + 1, size + 1):
                part = s[i : j]
                # print 'part: ', part, countUniqueChars(part)
                all_unique += countUniqueChars(part)
        
        return all_unique
    